html:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%}
    {% if letter_head and not no_letterhead %}
    {{ letter_head }}
    {% endif %}

    {%- if doc.meta.is_submittable and doc.docstatus==2-%}
    <div class="alert alert-danger text-center">
        <h4 style="margin: 0px;">{{ _("CANCELLED") }}</h4></div>
    {%- endif -%}

{%- endmacro -%}

<!--<small>-->

<style>
	.print-format table, .print-format tr, 
	.print-format td, .print-format div, .print-format p {
		font-family: Monospace;
                font-size: medium;                
		vertical-align: top;
	}
	@media screen {
		.print-format {
			width: 8.5in;
			padding: 0.05in;                        
			min-height: 5.5in;
		}
	}
</style>

{%- set n = 9 -%} <!-- number of items in one page -->
{%- set my_date = doc.get_formatted("posting_date") -%}

<!-- header information -->
<table style="line-height:100%;vertical-align: top;" cell-padding=0 width=100%>
        <tr valign=top>
            <td width=60% style="font-size:135%;">{{ add_header(0,1,doc,letter_head, no_letterhead) or '' }}</td>
            <td width=40% rowspan=2>Shipped To: <b><br>{{ doc.customer or ''}}</b><br><br>{{ doc.shipping_address or '' }}</td>
        </tr>       
        <tr>
            <td width=60%><b>NO. SURAT JALAN : {{ doc.name }} / {{ my_date or '' }}</b></td>

        </tr>

</table>

<!-- Items table -->
<!-- <table class="table table-condensed table-hover table-bordered"> -->
<table style="line-height:90%;border-top:1px solid;border-bottom:1px solid;cell-padding:0;width:100%">
        <tr style="border-bottom:1px solid;">
            <th>No</th>
            <th>Description</th>
            <th>Qty</th>
            <th>Unit of Measure / UOM</th>

        </tr>

        {%- if (doc.items|length <= n) -%} 
        {%- set page_size = 1 -%}
        {%- elif (doc.items|length == n+1) -%} 
        {%- set page_size = 1 -%}
        {%- set n = n+1 -%}
        {%- elif ((doc.items|length > n+1) and (doc.items|length <= (n*2))) -%} 
        {%- set page_size = 2 -%}
        {%- elif ((doc.items|length % n == 0)) -%} 
        {%- set page_size = doc.items|length / n -%}
        {% else %}
         {%- set page_size = (doc.items|length // n)+1 -%}
        {%- endif -%} 

         {%- for i in range(0,page_size * n) -%}

        {%- set row = doc.items[i] -%}

        {% if row != null -%}

        <tr>

            <td style="width: 5%;">{{ row.idx }}</td>
            <td style="width: 70%;">{{ row.description or '' }}       
             </td>
            <td style="width: 10%;">{{ row.get_formatted("qty") or '' }} </td>
             <td style="width: 15%;">{{ row.stock_uom }}  </td>

        </tr>
        {% else %}
		<tr>
                        <td> &nbsp; </td>
                        <td> &nbsp; </td>
                        <td> {{ _(" ") }} </td>
                        <td> {{ _(" ") }} </td>

                </tr>
                {%- endif %}

<!-- page-break here -->
       {% if ((i >= n) and (i % n) == 0) -%}

       </table>

<div class="page-break"></div> 
<!-- header info -->
<!-- header information -->
<table style="line-height:100%;vertical-align: top;" cell-padding=0 width=100%>
        <tr valign=top>
            <td width=60% style="font-size:135%;">{{ add_header(0,1,doc,letter_head, no_letterhead) or '' }}</td>
            <td width=40% rowspan=2>Shipped To: <b><br>{{ doc.customer or ''}}</b><br><br>{{ doc.shipping_address or '' }}</td>
        </tr>       
        <tr>
            <td width=60%><b>NO. SURAT JALAN : {{ doc.name }} / {{ my_date or '' }}</b></td>

        </tr>

</table>

<!-- Items table header -->
<table style="line-height:90%;border-top:1px solid;border-bottom:1px solid;cell-padding:0;width:100%">
		<tr style="border-bottom:1px solid;">
		    <th>No</th>
		    <th>Description</th>
		    <th>Qty</th>
                    <th>Unit of Measure / UOM</th>

		</tr>
        {%- endif %}

	{%- endfor -%}
</table>

